"use client";

import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { useEffect, useState } from "react";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix for missing marker icons in Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
  iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
  shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
});

export default function Map() {
  const [toilets, setToilets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchToilets() {
      try {
        const response = await fetch("http://127.0.0.1:5000/api/list");
        const data = await response.json();
        setToilets(data);
      } catch (error) {
        console.error("Error fetching toilets:", error);
      } finally {
        setLoading(false);
      }
    }

    fetchToilets();
  }, []);

  if (loading) {
    return <div>Loading map...</div>;
  }

  return (
    <section style={{ height: "100vh", width: "100%" }}>
      <h1 style={{ textAlign: "center", marginBottom: "1rem" }}>Find Nearby Toilets</h1>
      <MapContainer
        center={[9.96,76.29]} // Default center coordinates (update based on your area)
        zoom={14}
        style={{ height: "80vh", width: "100%" }}
      >
        {/* Tile layer for map background */}
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        {/* Markers for each toilet */}
        {toilets.map((toilet) => (
          <Marker key={toilet.id} position={[toilet.coordinates.lat, toilet.coordinates.lng]}>
            <Popup>
              <h3>{toilet.name}</h3>
              <p><strong>Location:</strong> {toilet.location}</p>
              <p><strong>Condition:</strong> {toilet.condition}</p>
              <p>
                <strong>Facilities:</strong>{" "}
                {toilet.facilities
                  ? `${toilet.facilities.baby_changing ? "Baby Changing Station " : ""}${
                      toilet.facilities.wheelchair_accessible ? "Wheelchair Accessible" : ""
                    }`
                  : "No special facilities"}
              </p>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </section>
  );
}
