import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

export default function ToiletDetails() {
  const [toilet, setToilet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchToiletDetails() {
      try {
        const response = await fetch(`http://127.0.0.1:5000/api/list`);
        if (!response.ok) {
          throw new Error('Toilet not found');
        }
        const data = await response.json();
        // Find the toilet with id from the array
        const selectedToilet = data.find(t => t.id === parseInt(id));
        if (!selectedToilet) {
          throw new Error('Toilet not found');
        }
        setToilet(selectedToilet);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchToiletDetails();
  }, [id]);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-[50vh]">
        <div className="text-xl">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center gap-4 min-h-[50vh] pt-10">
        <div className="text-xl text-red-600">{error}</div>
        <button 
          onClick={() => navigate('/')}
          className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
        >
          Back to Map
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto bg-white rounded-lg shadow-md p-6">
      <div className="flex justify-between items-start mb-6">
        <h1 className="text-3xl font-bold text-gray-800">{toilet.name}</h1>
        <button 
          onClick={() => navigate('/')}
          className="bg-gray-100 hover:bg-gray-200 text-gray-600 px-4 py-2 rounded"
        >
          Back to Map
        </button>
      </div>

      <div className="space-y-4">
        <div className="bg-gray-50 p-4 rounded">
          <h2 className="text-xl font-semibold mb-2 text-gray-700">Location Details</h2>
          <p className="text-gray-600">{toilet.location}</p>
          <p className="text-gray-600 mt-1">
            Coordinates: {toilet.coordinates.lat}, {toilet.coordinates.lng}
          </p>
        </div>

        <div className="bg-gray-50 p-4 rounded">
          <h2 className="text-xl font-semibold mb-2 text-gray-700">Status</h2>
          <p className="text-gray-600">
            <span className="font-medium">Condition:</span> {toilet.condition}
          </p>
        </div>

        <div className="bg-gray-50 p-4 rounded">
          <h2 className="text-xl font-semibold mb-2 text-gray-700">Facilities</h2>
          {toilet.facilities ? (
            <ul className="space-y-1 text-gray-600">
              {toilet.facilities.baby_changing && (
                <li>✓ Baby Changing Station</li>
              )}
              {toilet.facilities.wheelchair_accessible && (
                <li>✓ Wheelchair Accessible</li>
              )}
              {(!toilet.facilities.baby_changing && !toilet.facilities.wheelchair_accessible) && (
                <li>No special facilities available</li>
              )}
            </ul>
          ) : (
            <p className="text-gray-600">No facilities information available</p>
          )}
        </div>
      </div>
    </div>
  );
}
