// Map.js
import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import Sidebar from './Sidebar'; // Default import
import './Map.css'; // Your updated styling for the map

const toilets = [
  {
    id: 1,
    name: 'Toilet 1',
    latitude: 28.6139,
    longitude: 77.2090,
    address: 'Delhi, India',
    is_accessible: true,
    is_free: false,
  },
  {
    id: 2,
    name: 'Toilet 2',
    latitude: 28.6140,
    longitude: 77.2100,
    address: 'Delhi, India',
    is_accessible: false,
    is_free: true,
  },
];

const Map = () => {
  const [selectedToilet, setSelectedToilet] = useState(null);

  const handleMarkerClick = (toilet) => {
    setSelectedToilet(toilet);
  };

  const handleCloseSidebar = () => {
    setSelectedToilet(null);
  };

  return (
    <div className="map-container">
      <div className="map-wrapper">
        <MapContainer center={[28.6139, 77.2090]} zoom={13} style={{ width: '100%', height: '100%' }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          {toilets.map((toilet) => (
            <Marker
              key={toilet.id}
              position={[toilet.latitude, toilet.longitude]}
              eventHandlers={{
                click: () => handleMarkerClick(toilet),
              }}
            />
          ))}
        </MapContainer>
      </div>

      {/* Sidebar */}
      <div className={`sidebar-wrapper ${selectedToilet ? '' : 'hidden'}`}>
        <Sidebar toilet={selectedToilet} onClose={handleCloseSidebar} />
      </div>
    </div>
  );
};

export default Map;
