import React from "react";
import { motion } from "framer-motion";
import "./Home.css";

function Home() {
  return (
    <div className="home-container">
      {/* Hero Section - Fullscreen */}
      <section className="home-section">
        <div className="title-section">
          <motion.div
            className="hero-content"
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.5, duration: 0.8 }}
          >
            <h1 className="main-title">
              <span className="rush">RUSH</span>
              <span className="room">
                ROOM<span className="dot">.</span>
              </span>
            </h1>
            <p className="subtitle">Find Your Comfort, Anywhere</p>
          </motion.div>
        </div>
      </section>

      {/* Overview Section - Animated Scroll */}
      <motion.section
        className="overview-section"
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <div className="overview-content">
          <h2>About RushRoom</h2>
          <p>
            RushRoom helps you locate clean, accessible, and convenient restrooms wherever you go. 
            Say goodbye to the stress of searching for a restroom in unfamiliar places. 
            With real-time data, user reviews, and a community-driven approach, 
            we make restroom discovery easier than ever.
          </p>
        </div>
      </motion.section>
    </div>
  );
}

export default Home;
