import React, { useState } from "react";
import { X, ThumbsUp, ThumbsDown } from "lucide-react";
import "./AddReview.css"

const cleanlinessOptions = ["Pristine", "Clean", "Acceptable", "Unsanitary", "Avoid"];
const issueOptions = ["Broken Flush", "No Water", "Unclean", "No Toilet Paper", "Odor", "Other"];

const AddReview = ({ id ,open, onClose }) => {
    const [name, setName] = useState("");
    const [rating, setRating] = useState("");
    const [cleanliness, setCleanliness] = useState("Clean");
    const [issues, setIssues] = useState([]);
    const [review, setReview] = useState("");
    const [liked, setLiked] = useState(null); // true for like, false for dislike

    const handleIssueChange = (event) => {
        const value = event.target.value;
        setIssues(prev => prev.includes(value) ? prev.filter(i => i !== value) : [...prev, value]);
    };

    return open ? (
        <div className="modal-overlay">
            <div className="modal-box">
                <div className="modal-header">
                    <h2>Add Review</h2>
                    <button className="modal-close" onClick={onClose}>
                        <X size={24} />
                    </button>
                </div>
                <div className="modal-content">
                    <div className="modal-form">
                        <label>Name:</label>
                        <input type="text" placeholder="John Doe" value={name} onChange={(e) => setName(e.target.value)} className="review-input" />

                        <label>Rating (1-5):</label>
                        <input type="number" placeholder="3" min="1" max="5" value={rating} onChange={(e) => setRating(e.target.value)} className="review-input" />

                        <label>Cleanliness:</label>
                        <select value={cleanliness} onChange={(e) => setCleanliness(e.target.value)} className="review-input">
                            {cleanlinessOptions.map((option) => (
                                <option key={option} value={option}>{option}</option>
                            ))}
                        </select>

                        <label>Raise Issues:</label>
                        <div className="issues-list">
                            {issueOptions.map((issue) => (
                                <label key={issue} className="issue-checkbox">
                                    <input type="checkbox" value={issue} checked={issues.includes(issue)} onChange={handleIssueChange} />
                                    {issue}
                                </label>
                            ))}
                        </div>

                        <label>Review:</label>
                        <textarea placeholder="Write your review..." value={review} onChange={(e) => setReview(e.target.value)} className="review-input"></textarea>

                        <div className="like-dislike">
                            <button className={`like-button ${liked === true ? "active" : ""}`} onClick={() => setLiked(true)}>
                                <ThumbsUp size={20} /> Like
                            </button>
                            <button className={`dislike-button ${liked === false ? "active" : ""}`} onClick={() => setLiked(false)}>
                                <ThumbsDown size={20} /> Dislike
                            </button>
                        </div>

                        <button className="submit-review">Submit</button>
                    </div>
                </div>
            </div>
        </div>
    ) : null;
};

export default AddReview;
