import React, { useEffect, useState } from "react";
import { supabase } from "../backend/supabaseClient";
import { useParams, useLocation } from "react-router-dom";
import "./Reviews.css";
import { Separator } from "@radix-ui/react-separator";
import { Plus ,Quote } from "lucide-react";
import { formatDistanceToNowStrict } from "date-fns";



function Reviews() {
    const { id } = useParams();
    const { name, place } = useLocation().state;
    const [reviews, setReviews] = useState([]);

    useEffect(() => {
        const fetchReviews = async () => {
            const { data, error } = await supabase
                .from("reviews") // This should come before .select()
                .select("*") // Fetch all columns
                .eq("toilet_id", id);

            if (error) {
                console.error("Error fetching reviews:", error);
            } else {
                setReviews(data);
            }
        };

        fetchReviews();
    }, [id]); // Add id as a dependency

    const formatReviewTime = (dateString) => {
        return formatDistanceToNowStrict(new Date(dateString), { addSuffix: true });
      };
    return (
        <div className="container">
            <div className="review-page">
                <div className="review-grid">
                    <div className="review-left">
                        <div className="title">
                            <p>Review for</p>
                            <span className="titlehigh">{name},</span>
                            <span className="titlehigh place"> {place}</span>
                        </div>
                        <Separator className="SeparatorRoot" style={{ margin: "20px 24px" }} />
                        <button className="add-review-button">
                            <Plus size={24} />
                            <span>Add Review</span>
                        </button>
                    </div>
                    <div className="review-right">
                        <div className="review-list">
                            {reviews.length > 0 ? (
                                reviews.map((rev) => (
                                    <div key={rev.id} className="review-card">
                                        <p><Quote size={24} style={{ transform: "scaleX(-1)" }} />{rev.review}</p>
                                        <p>Rating: {rev.rating} </p>
                                        <p><strong>{rev.user_name}</strong></p>
                                        <p>{formatReviewTime(rev.created_at)}</p>
                                    </div>
                                ))
                            ) : (
                                <p>No reviews yet.</p>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Reviews;
