import React ,  {useEffect , useState} from "react";
import { supabase } from "../backend/supabaseClient";
import { useParams  , useLocation } from "react-router-dom";
import "./Reviews.css";
import { Separator } from "@radix-ui/react-separator";
import { Plus } from "lucide-react";


useEffect(()=>{
    const fetchReviews = async ()=>{
        const {data , error} = await supabase
        .select()
        .from("reviews")
        .eq("toilet_id"  , id)
    }
},[])


function Reviews (){

    const {id} = useParams();
    const{name , place} = useLocation().state;



    return (
        <div className="container">

            <div  className="review-page">
                <div className="review-grid">
                    <div className="review-left">
                    <div className="title"><p>Review for</p> <span className="titlehigh">{name},</span><span className="titlehigh place"> {place}</span></div>
                    <Separator className="SeparatorRoot" style={{ margin: "20px 24px" }} />
                    <button className="add-review-button"><Plus size={24}/><span>Add Review</span></button>
                    </div>
                    <div className="review-right">

                        </div>
                </div>
            </div>

        </div>
    )
}

export default Reviews;
