import React, { useState } from 'react';
import { motion } from 'framer-motion';
import './Home.css';

function Home() {
  const [activeSection, setActiveSection] = useState(0);

  return (
    <div className="home-container">
      <div className={`home-section ${activeSection === 0 ? 'active' : ''}`}>
        <div className="title-section">
          <motion.div 
            className="hero-content"
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.5, duration: 0.8 }}
          >
            <h1 className="main-title">
              <span className="rush" style={{textAlign: 'left'}}>RUSH</span>
              <span className="room" style={{marginLeft: '0'}}>ROOM<span className="dot">.</span></span>
            </h1>
            <p className="subtitle">Find Your Comfort, Anywhere</p>
          </motion.div>
        </div>
      </div>

      <div className={`overview-section ${activeSection === 1 ? 'active' : ''}`}>
        <div className="overview-content">
          <h2>About RushRoom</h2>
          <p>
              RushRoom is your ultimate companion in finding clean, accessible, 
              and convenient restrooms wherever you are. Our mission is to eliminate 
              the stress of finding a suitable toilet in unfamiliar places, providing 
              real-time information, user reviews, and a community-driven approach 
              to restroom discovery.
            </p>
        </div>
      </div>
    </div>
  );
}

export default Home;
