import "./ToiletDetails.css";
import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import { supabase } from "../backend/supabaseClient";
import { 
  ChevronLeft, 
  ChevronRight,
  Star,
  ShowerHead,
  Clock,
  MapPin,
  Phone,
  Calendar
} from "lucide-react";

const ToiletDetails = () => {
  const [toilet, setToilet] = useState(null);
  const [direction, setDirection] = useState(0);
  const { id } = useParams();
  const navigate = useNavigate();
  const numToilets = 10;

  const fetchToilet = async (toiletId) => {
    const { data, error } = await supabase
      .from("toilets")
      .select("*")
      .eq("id", toiletId)
      .single();

    if (!error) setToilet(data);
    return !error;
  };

  useEffect(() => {
    fetchToilet(id);
  }, [id]);

  const handleCarousel = (dir) => {
    setDirection(dir);
    const currentId = parseInt(id);
    let nextId;

    if (dir > 0) {
      nextId = currentId === numToilets ? 1 : currentId + 1;
    } else {
      nextId = currentId === 1 ? numToilets : currentId - 1;
    }

    navigate(`/toilet/${nextId}`);
  };

  const variants = {
    enter: (direction) => ({
      x: direction > 0 ? 1000 : -1000,
      opacity: 0
    }),
    center: {
      zIndex: 1,
      x: 0,
      opacity: 1
    },
    exit: (direction) => ({
      zIndex: 0,
      x: direction < 0 ? 1000 : -1000,
      opacity: 0
    })
  };

  const swipeConfidenceThreshold = 10000;
  const swipePower = (offset, velocity) => {
    return Math.abs(offset) * velocity;
  };

  if (!toilet) return <div className="toilet-details">Loading...</div>;

  return (
    <div className="carousel-container">
      <AnimatePresence initial={false} custom={direction}>
        <motion.div
          className="toilet-card"
          key={toilet.id}
          custom={direction}
          variants={variants}
          initial="enter"
          animate="center"
          exit="exit"
          transition={{
            x: { type: "spring", stiffness: 300, damping: 30 },
            opacity: { duration: 0.2 }
          }}
          drag="x"
          dragConstraints={{ left: 0, right: 0 }}
          dragElastic={1}
          onDragEnd={(e, { offset, velocity }) => {
            const swipe = swipePower(offset.x, velocity.x);

            if (swipe < -swipeConfidenceThreshold) {
              handleCarousel(1);
            } else if (swipe > swipeConfidenceThreshold) {
              handleCarousel(-1);
            }
          }}
        >
          <div className="card-grid">
            {/* Left Section */}
            <div className="image-section">
              <img 
                src="/assets/toilet-default.jpg" 
                alt={toilet.name}
                className="main-image"
              />
              <div className="image-overlay">
                <h2 className="toilet-name">{toilet.name}</h2>
                <div className="location">
                  <MapPin className="icon" />
                  <span>{toilet.place}</span>
                </div>
                {toilet.average_rating && (
                  <div className="rating">
                    <Star className="icon filled" />
                    <span>{toilet.average_rating}</span>
                  </div>
                )}
                <div className="gender-tag">
                  {toilet.gender_availability.replace('_', ' ')}
                </div>
              </div>
            </div>

            {/* Right Section */}
            <div className="details-section">
              {/* Shower Highlight Box */}
              <div className={`shower-box ${toilet.shower ? 'available' : 'unavailable'}`}>
                <ShowerHead className="icon" />
                <span>Shower {toilet.shower ? 'Available' : 'Not Available'}</span>
              </div>

              <div className="details-grid">
                <div className="detail-item">
                  <Clock className="icon" />
                  <span>{toilet.opening_hours} - {toilet.closing_hours}</span>
                </div>

                <div className="detail-item category">
                  <span className="badge">{toilet.category}</span>
                </div>

                <div className="detail-item">
                  <span className="price-label">Price:</span>
                  <span className={`price-value ${toilet.free ? 'free' : ''}`}>
                    {toilet.free ? 'Free' : `Rs. ${toilet.price}`}
                  </span>
                </div>

                {toilet.caretaker_no && (
                  <div className="detail-item">
                    <Phone className="icon" />
                    <span>{toilet.caretaker_no}</span>
                  </div>
                )}

                {toilet.last_maintenance && (
                  <div className="detail-item">
                    <Calendar className="icon" />
                    <span>Last maintained: {new Date(toilet.last_maintenance).toLocaleDateString()}</span>
                  </div>
                )}
              </div>
            </div>
          </div>
        </motion.div>
      </AnimatePresence>

      <button 
        className="carousel-button prev"
        onClick={() => handleCarousel(-1)}
      >
        <ChevronLeft size={24} />
      </button>

      <button 
        className="carousel-button next"
        onClick={() => handleCarousel(1)}
      >
        <ChevronRight size={24} />
      </button>

      <motion.button 
        className="back-button"
        onClick={() => navigate(-1)}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        Go Back
      </motion.button>
    </div>
  );
};

export default ToiletDetails;
