import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { supabase } from "../backend/supabaseClient";

const ToiletDetails = () => {
  const [toilet, setToilet] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchDetails() {
      const { data, error } = await supabase
        .from("toilets")
        .select("*")
        .eq("id", id)
        .single(); // Fetch a single object instead of an array

      if (error) {
        console.error("Error fetching toilet details:", error);
      } else {
        setToilet(data);
      }
    }

    fetchDetails();
  }, [id]); // Depend on `id`, not `toilet.id`

  if (!toilet) {
    return <p>Loading toilet details...</p>;
  }

  return (
    <div>
      <h1>{toilet.name}</h1>
      <p><strong>Location:</strong> {toilet.place}</p>
      <p><strong>Category:</strong> {toilet.category}</p>
      <p><strong>Availability:</strong> {toilet.gender_availability}</p>
      <p><strong>Price:</strong> {toilet.free ? "Free" : `Rs. ${toilet.price}`}</p>
      <p><strong>Timings:</strong> {toilet.opening_hours} - {toilet.closing_hours}</p>
      <p><strong>Shower:</strong> {toilet.shower ? "Available" : "Not Available"}</p>
      <p><strong>Rating:</strong> {toilet.average_rating ?? "No ratings yet"}</p>
      <p><strong>Contact:</strong> {toilet.contact_caretaker ?? "Not available"}</p>
      <p><strong>Last Maintenance:</strong> {toilet.last_maintenance ?? "Unknown"}</p>
      {toilet.image && <img src={toilet.image} alt={toilet.name} style={{ width: "100%", maxHeight: "300px", objectFit: "cover" }} />}
      <button onClick={() => navigate(-1)}>Go Back</button>
    </div>
  );
};

export default ToiletDetails;
