import React from 'react';
import './Sidebar.css';  // Your custom sidebar styling

const Sidebar = ({ toilet, onClose }) => {
  return (
    <div className="sidebar">
      <button className="close-btn" onClick={onClose}>X</button>
      <h2>{toilet.name}</h2>
      <p><strong>Address:</strong> {toilet.address}</p>
      <p><strong>Accessible:</strong> {toilet.is_accessible ? 'Yes' : 'No'}</p>
      <p><strong>Free:</strong> {toilet.is_free ? 'Yes' : 'No'}</p>
      {/* You can add more toilet details here */}
    </div>
  );
};

export default Sidebar;
