import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { useEffect, useRef, useState } from "react";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import Sidebar from "./Sidebar";
import RecenterMap from "./RecenterMap";
import MarkerClusterGroup from "react-leaflet-markercluster";
import "./Map.css";
import Filter from "./Filter";

// Fix Leaflet marker icon issues
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
  iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
  shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
});

export default function Map() {
  const [toilets, setToilets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedToiletIndex, setSelectedToiletIndex] = useState(0); // Index for navigation
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [location, setLocation] = useState([9.965579, 76.24275]);
  const [userMarker, setUserMarker] = useState(null);
  const markerRefs = useRef([]); // Store marker references

  useEffect(() => {
    async function fetchToilets() {
      try {
        const response = await fetch("http://127.0.0.1:5000/api/list");
        if (!response.ok) throw new Error("Failed to fetch toilets.");
        const data = await response.json();
        setToilets(data);
      } catch (error) {
        console.error("Error fetching toilets:", error.message);
      } finally {
        setLoading(false);
      }
    }
    fetchToilets();
  }, []);

  const handleFetchLocation = () => {
    if (!navigator.geolocation) {
      alert("Geolocation is not supported by your browser.");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const currentLocation = [position.coords.latitude, position.coords.longitude];
        setLocation(currentLocation);
        setUserMarker(currentLocation);
      },
      (error) => {
        console.error("Error fetching location:", error.message);
        alert("Unable to fetch location. Please check your permissions.");
      }
    );
  };

  const handleNext = () => {
    const nextIndex = (selectedToiletIndex + 1) % toilets.length;
    setSelectedToiletIndex(nextIndex);
    markerRefs.current[nextIndex]?.openPopup();
  };

  const handlePrev = () => {
    const prevIndex = (selectedToiletIndex - 1 + toilets.length) % toilets.length;
    setSelectedToiletIndex(prevIndex);
    markerRefs.current[prevIndex]?.openPopup();
  };

  if (loading) {
    return <div className="loading-container">Loading...</div>;
  }

  return (
    <div className="map-page">
      <div className="map-controls">
        <button className="location-btn" onClick={handleFetchLocation}>
          Get My Location
        </button>
        <button className="prev-btn" onClick={handlePrev}>
          Prev
        </button>
        <button className="next-btn" onClick={handleNext}>
          Next
        </button>
      </div>

      <div className="map-container">
        <MapContainer center={location} zoom={15} className="leaflet-map">
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          <MarkerClusterGroup
            iconCreateFunction={(cluster) => {
              const count = cluster.getChildCount();
              return L.divIcon({
                html: `<div style="background-color: blue; color: white; border-radius: 70%; width: 30px; height: 30px; display: flex; justify-content: center; align-items: center;">${count}</div>`,
                className: "custom-cluster-icon",
              });
            }}
          >
            {toilets.map((toilet, index) => (
              <Marker
                key={toilet.id}
                position={[toilet.coordinates.lat, toilet.coordinates.lng]}
                ref={(el) => (markerRefs.current[index] = el)} // Store marker references
              >
                <Popup>
                  <div className="popup-content">
                    <h3>{toilet.name}</h3>
                    <p>{toilet.location}</p>
                    <button onClick={() => setIsSidebarOpen(true)}>
                      View Details
                    </button>
                  </div>
                </Popup>
              </Marker>
            ))}
          </MarkerClusterGroup>
          {userMarker && (
            <Marker position={userMarker}>
              <Popup>Your current location</Popup>
            </Marker>
          )}
          <RecenterMap location={location} />
        </MapContainer>
      </div>

      <Sidebar
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
        toilet={toilets[selectedToiletIndex]}
      />
    </div>
  );
}
