import React from "react";
import './Sidebar.css';
import { useNavigate } from "react-router-dom";
export default function Sidebar({ isOpen, onClose, toilet }) {
  if (!toilet) return null;
  const currentpage = useNavigate();
  return (<>
    {isOpen && (
      <div 
        className="sidebar-overlay"
        onClick={onClose}
      />
    )}
    <div className={`sidebar ${isOpen ? 'open' : ''}`}>
          <button className="close-button" onClick={onClose}>
            Ã—
          </button>
        <div className="sidebar-content">
      <h2>{toilet.name}</h2>
      <p>Location: {toilet.name}</p>
      <p>{toilet.place}</p>
      <p>{toilet.free?"Free": " Paid"}</p>
      <p>{!toilet.free && `Rs.${toilet.price}`}</p>
      <p>{`From ${toilet.opening_hours} to ${toilet.closing_hours}`}</p>
      <button onClick={()=>{currentpage(`/toilets/${toilet.id}`)}}>Click to know more</button>
    </div>
    </div>
    </>
  );
}
